// Create a 'base' ack message that can be modified and returned by the 
// channel post-processor 
//MSH
tmp['MSH']['MSH.3']['MSH.3.1'] = msg['MSH']['MSH.5']['MSH.5.1'].toString();
tmp['MSH']['MSH.4']['MSH.4.1'] = msg['MSH']['MSH.6']['MSH.6.1'].toString();
tmp['MSH']['MSH.5']['MSH.5.1'] = msg['MSH']['MSH.3']['MSH.3.1'].toString();
tmp['MSH']['MSH.6']['MSH.6.1'] = msg['MSH']['MSH.4']['MSH.4.1'].toString();

tmp['MSH']['MSH.10']['MSH.10.1'] = msg['MSH']['MSH.10']['MSH.10.1'].toString();
tmp['MSH']['MSH.11']['MSH.11.1'] = msg['MSH']['MSH.11']['MSH.11.1'].toString();
tmp['MSH']['MSH.12']['MSH.12.1'] = msg['MSH']['MSH.12']['MSH.12.1'].toString();
tmp['MSH']['MSH.7']['MSH.7.1'] = DateUtil.getCurrentDate("yyyyMMddHHmmss");

// MSA
tmp['MSA']['MSA.2']['MSA.2.1'] = msg['MSH']['MSH.10']['MSH.10.1'].toString();
tmp['MSA']['MSA.3']['MSA.3.1'] = "Succesfully journaled and en-queued";
// Save the message
channelMap.put('templateAck',tmp);

