channel.postprocessingScript:
// This script executes once after a message has been processed

var cumulativeSuccess = true;
var badDestMessage = "";

try {
	var responseMapKeys = responseMap.keySet();	
	var destIter = responseMapKeys.iterator();
	var ackMessage = channelMap.get('templateAck');


	// Iterate each destination reponse in the response map
	// all destination must succeed or filter for an ack response
	while (destIter.hasNext()) {
     	var destinationName = destIter.next();
     	var response = responseMap.get(destinationName);	
     	var status = response.getStatus();

//04-10/10 HS
//logger.error(destinationName + ": " + status );

     	if(status == 'SUCCESS' || status == 'FILTERED')
     	{
	     	cumulativeSuccess = cumulativeSuccess && true;
     	}
	 	else
	 	{      
            cumulativeSuccess = false;
         	badDestMessage += ' ' +  destinationName;
     	}
	}

	// Modify the ACK message constructed by the journal destination
    // transformer if one of the destinations failed
	// In this case AR is intended to cause the sending system 
    // to retry sending the message.

	if(!cumulativeSuccess) {
		ackMessage['MSA']['MSA.1']['MSA.1.1'] = 'AR';
    	ackMessage['MSA']['MSA.3']['MSA.3.1'] = 'TIE End point failed:' + badDestMessage; 
	}	

	// Convert ACK message from XML to HL7 ready to resend
	ackMessage = SerializerFactory.getHL7Serializer(false, false, true).fromXML(ackMessage);

	//Put the message onto the response map for the channel reader to find
	//The channel reader responds from must be pointed at 'ACK Message'
	//optionally stop the channel if one of the destinations has 'failed'
	if(cumulativeSuccess){
		responseMap.put('ACK Message', ResponseFactory.getSuccessResponse(ackMessage));
	}
	else {
		responseMap.put('ACK Message', ResponseFactory.getFailureResponse(ackMessage));
		// comment the following line if you want to return an AR NAK to the sender if error occured
        throw new Error('Enqueue failed for destination ' + badDestMessage);
	}
}
catch (e)
{
	logger.error("An exception occurred in the post-processor script for the channel [" + getChannelName() + "]. Error name: " + e.name + ". Error message: " + e.message);	
    stopChannel(channelId);
}
finally {
	return;
}

channel.deployScript:
try
{	
	//	30-10-2010 HS    Hardcode the name of the channel	
	globalMap.put(channelId,'CRIS_OMG-ORU_Q');

	setQueueDBConnection();

	//Initialize channel	
	initializeChannelQueueing();

	return;
}
catch (e)
{
	logger.error("An exception occurred in the Deploy script of channel" + getChannelName() + " . Error name: " + e.name + ". Error message: " + e.message);
}

channel.shutdownScript:
// This script executes once when the mule engine is stopped
// You only have access to the globalMap here to persist data
return;
