step.script:
// prefix ORH filler order numbers if this is a reflex order
// so that they can be distinguised for NOC numbers

try {
	if(msg['ORC']['ORC.1']['ORC.1.1'].toString() == 'NW') {
		var fillerOrderNumber = msg['ORC']['ORC.3']['ORC.3.1'].toString();

		if(fillerOrderNumber.length > 0) {
			msg['ORC']['ORC.3']['ORC.3.1'] = 'ORH' + fillerOrderNumber;
		}
	}
}
catch (e) {
	logger.error("Channel " + getChannelName() + " ( Prefix Filler Order Number ) " + e.name + ": " + e.message);
	throw e;
}

