properties.property:
try
{
	// ---=== PROMOTION LOGIC BEGIN

	//HS 11-10-10
	//The value of this variable should only be altered when there is more than one
	//enqueueing destination for this channel, otherwise it can be left unchanged.	
	var channelDestination = 'enqueue_ORM';

    //get the attribute type map from the globalMap
	// md 23/09/10 var messageAttributeTypeMap = globalMap.get(getChannelName() + "_promotableCollection");
    var messageAttributeTypeMap = getPromotableCollection();

	//get destinationQueueMap variable that is held on global map. (see channelInitializer template)
	// md 23/09/10 var destinationQueueMap = globalMap.get(getChannelName() + "_destinationQueueMap");
    var destinationQueueMap = getDestinationQueueMap();

	//from the destinationQueueMap (which holds <K,V> pairs of <destination name, queue name>
	//to get the queue that this destination publishes to.

	//05-10-2010 HS
	//Modified code so channelDestination is hardcoded to enqueue to queue
	//var destinationQ = destinationQueueMap.get(channelDestination);
	var destinationQ = destinationQueueMap.get(channelDestination);

	//get the appropriate list of attribute types for the specific queue
	var messageAttributeTypeList = messageAttributeTypeMap.get(destinationQ);

	//extract the attribute values for this message		
//	var attributeList = Packages.nhs.ohis.si.queueing.Promoter.getPromotedData(channelMap.get('msgXML'), messageAttributeTypeList);
	//25-OCT-2011 HS    Modified code to use channel specific instance of promoter (not static method)
	var attributeList = getPromoter().getPromotedData(channelMap.get('msgXML'), messageAttributeTypeList);

	// ---=== PROMOTION LOGIC END

	// ---=== ENQUEUE LOGIC BEGIN

    //connection definitions are defined when channel is deployed.	    
/*
	Packages.nhs.ohis.si.queueing.MessageQueuer.putMessageOnQueue(getChannelName(),
																  channelDestination, 
                                                                  getQueueDBConnection(), 
                                                                  channelMap.get('msgXML'), 
                                                                  attributeList);
*/
	var lastMsgNum = Packages.nhs.ohis.si.queueing.MessageQueuer.putMessageOnQueue(getChannelName(),
																  channelDestination, 
                                                                  getQueueDBConnection(), 
                                                                  channelMap.get('msgXML'), 
                                                                  attributeList);

	setLastPubMsgNbr(destinationQ, lastMsgNum);
	// ---=== ENQUEUE LOGIC END



}
catch (e)
{
	 // Throw the error so that the the reponse object gets failure status used by the postprocessot
     // may need to filter out and not fail some of the exceptions returned by putMessageOnQueue
     // Leave error managment to the postprocessor that can alert and 
     // stop the chanell etc
     logger.error("An exception occurred in destination channel [" + getChannelName() + "] script. Error name: " + e.name + ". Error message: " + e.message);

	 throw e;
}

step.script:
// remove ORH MRN prefix and any RBF MRN

try {
	var index = 0;
	var delCount = 0;

	for each (pid3 in msg['PID']['PID.3'] ) {
		if (pid3['PID.3.5'].toString() == "MR"){
			if(pid3['PID.3.4'].toString() == "RTH") {
				if(pid3['PID.3.1'].toString().substr(0,3) == 'RTH') {
					pid3['PID.3.1'] = pid3['PID.3.1'].toString().substr(3);
				}
			}
			else
			{
				delete msg['PID']['PID.3'][index - delCount];
				delCount++;
			}
		}

		index++;
	}
}
catch (e) {
	logger.error("Channel " + getChannelName() + " ( enqueue_OMG-ORG PID3 ) " + e.name + ": " + e.message);
	throw e;
}

