entry.string:
// only pass ORU messages reported from ORH

try {
	var pass = false;

	// determine if this is an unsolicited result  
	var placerOrderNumber =  msg['OBR']['OBR.2']['OBR.2.1'].toString();

	if (placerOrderNumber.length < 1) {
		// lookup the rule for enqueuing this unsolicited message
		tranResult = translateWithDefault(getTranslator(), 
                                          "UNSOL_LOC_FILTER", 
                                          msg['ORC']['ORC.13']['ORC.13.1'].toString(), 
                                          "XXXX", 
                                          "N");
		// to value will either be Y or N
		pass = (tranResult.getToValue() == "Y"); 
	}
	else pass = true; // thsi is a solicited result

	return pass;
}
catch (e) {
	logger.error("Channel " + getChannelName() + " ( ORU and Unsolicited Results Referral Location Filter ) " + e.name + ": " + e.message);
	throw e;
}

