step.script:
//Proccess segments according to message event
try {

	var event = msg['MSH']['MSH.9']['MSH.9.2'].toString();
	// Redefine event according to rules
	//A34-A40	Transform event type for merge
	if (event == 'A34') {
		event = 'A40';
	}

	//MSH-T1	Convert to an update status message if patient discharged as deceased
	//			i.e. Patient Death Indicator is not blank

	else if (event == 'A03'){
		if (hasData(msg['PID']['PID.30']['PID.30.1'])) {
			event = 'A08';
		}
	}

   	//MRD 24/01: If A13 message set to A08 to ensure reverese DECEASED discharge is 
   	//           communicated to CRIS
	//MSH-T1b	Convert to an update status message if patient discharged as deceased
	//			i.e. Patient Death Indicator is not blank

	else if (event == 'A13') {
		event = 'A08'; 	
	}

	//Process segments according to event
	switch (event) {
		case 'A01' : //Admit patient
			//Transform required segments to temp
			tranMSH();
			tranEVN();	
			tranPID();	
			tranPV1();

			//Delete non-required segments
			delete tmp['PD1'];
			delete tmp['MRG'];
			delete tmp['SCH']; 
			break;	

		case 'A02' : // Transfer patient	
			//Transform required segments to temp
			tranMSH();
			tranEVN();		
			tranPID();
			tranPV1();

			//Delete non-required segments
			delete tmp['PD1'];
			delete tmp['MRG'];
			delete tmp['SCH']; 
			break;	

	    case 'A03' : // Discharge patient	
			//Transform required segments to temp
			tranMSH();
			tranEVN();			
			tranPID();

			//Delete non-required segments
			delete tmp['PD1'];
			delete tmp['PV1'];
			delete tmp['MRG'];
			delete tmp['SCH']; 
			break;				

		case 'A05' : // Pre-admit patient
			//Transform required segments to temp
			//Transform required segments to temp
			tranMSH();
			tranEVN();		
			tranPID();
			tranPV1();

			//Delete non-required segments
			delete tmp['PD1'];
			delete tmp['MRG'];
			delete tmp['SCH']; 
			break;	

		case 'A08' : // Update patient information
			//Transform required segments to temp
			tranMSH();
			tranEVN();			
			tranPID();
			tranPD1();

			//Delete non-required segments
			delete tmp['PV1'];
			delete tmp['MRG'];
			delete tmp['SCH']; 
			break;				

	 	case 'A28' : //Add person information
			//Transform required segments to temp
			tranMSH();
			tranEVN();			
			tranPID();
			tranPD1();

			//Delete non-required segments
			delete tmp['PV1'];
			delete tmp['MRG'];
			delete tmp['SCH']; 
			break;				

		case 'A31' : // Update person information
			//Transform required segments to temp
			tranMSH();
			tranEVN();			
			tranPID();
			tranPD1();

			//Delete non-required segments
			delete tmp['PV1'];
			delete tmp['MRG'];
			delete tmp['SCH']; 
			break;				


    	case 'A40' : // Merge patient
			//Transform required segments to temp
			tranMSH();	
			tranEVN();	
			tranPID();
			tranMRG();

			//Delete non-required segments
			delete tmp['PD1']; 
			delete tmp['PV1'];
			delete tmp['SCH']; 
			break;

		case 'S12' : // Merge patient
		case 'S13' : // Merge patient
			//Transform required segments to temp
			tranMSH();	
			tranPID();
			tranSCH();

			//Delete non-required segments
			delete tmp['EVN']; 
			delete tmp['PD1']; 
			delete tmp['PV1'];
			delete tmp['MRG'];
			break;

		default :
			break;
		}
}
catch (e) {
		throw new Error(getChannelName() + " ( Event Sorter ) " + e.name + ": " + e.message);
		stopChannel(channelId);
}

