step.script:
function tranMSH()  {
	try {
		//Pass all
		tmp['MSH'] = msg['MSH'];
		// set message event (see logic in main)
		tmp['MSH']['MSH.9']['MSH.9.2'] = event;
		//MSH-T2	Change HL7 version number
		tmp['MSH']['MSH.12']['MSH.12.1'] = "2.4";
	}

	catch (e) {
    	throw new Error(" in tranMSH " + e.name + ": " + e.message);
	}
}

