function tranEVN()  {
	try {
		//Pass all
		tmp['EVN'] = msg['EVN'];
		tmp['EVN']['EVN.1']['EVN.1.1'] = event;

		//EVN-T1	Translation event reason code (cs 285)
		tmp['EVN']['EVN.4']['EVN.4.1'] = '""';
	}

	catch (e)
	{
    	throw new Error(" in tranEVN " + e.name + ": " + e.message);
	}
}

