step.script:
function tranPID() {
	try {
		// Some working variables
		var index=0;


    	//Pass Set ID 
		tmp['PID']['PID.1']['PID.1.1']=msg['PID']['PID.1']['PID.1.1'].toString();


		//PID-T2	Output NOC mrn and nhs number only in that order
		tmp['PID']['PID.3'][0]['PID.3.1']=getPID(msg['PID']['PID.3'],'NOC-MRN','MRN','PID.3.4','PID.3.5','PID.3.1');
		// MRD 17/01/11: HSS require asigning authority for NOC MRN of RBF03
		tmp['PID']['PID.3'][0]['PID.3.4']='RBF03';
		tmp['PID']['PID.3'][0]['PID.3.5']='MR';

		// make nhs number 10 spaces if absent 
		var nhsnumber=getPID(msg['PID']['PID.3'],'NHSNBR','NHSNMBR','PID.3.4','PID.3.5','PID.3.1');
		if (nhsnumber!='') {
			tmp['PID']['PID.3'][1]['PID.3.1']=nhsnumber;
		}
		else	{
			tmp['PID']['PID.3'][1]['PID.3.1']='          ';
		}
		tmp['PID']['PID.3'][1]['PID.3.4']='NHS';
		tmp['PID']['PID.3'][1]['PID.3.5']='NH';


		//PID-T3	Prefix ORH MRN's with NACS code
		//          *** NOT APPLICABLE for NOC RIS ***


		//PID-T7	Only output "CURRENT" name
		//PID-T8	Append given name with initial from middle name
		index=findFirst('CURRENT',msg['PID']['PID.5'],'PID.5.7');
		if (index > -1) {
			tmp['PID']['PID.5'][0]['PID.5.1']=msg['PID']['PID.5'][index]['PID.5.1'].toString();
			tmp['PID']['PID.5'][0]['PID.5.2']=msg['PID']['PID.5'][index]['PID.5.2'].toString();
			// MRD 28/09/11 Ensure middlename is blank to avoid consfusion with 'two word' forename
			tmp['PID']['PID.5'][0]['PID.5.3']="";
			tmp['PID']['PID.5'][0]['PID.5.5']=msg['PID']['PID.5'][index]['PID.5.5'].toString();
		}	


		// Pass Date of Birth
		tmp['PID']['PID.7']['PID.7.1']=msg['PID']['PID.7']['PID.7.1'].toString();


		//PID-T14	Translate sex code (cs 57)


    	// MRD 12-01-11 get sex translation using switch statement pending translator bug fix 
		var fromCode = msg['PID']['PID.8']['PID.8.1'].toString();
    	var resultOutput = '';
		switch(fromCode) {
			case '0': resultOutput = 'U';
            	      break;
        	case '1': resultOutput = 'M';
            	      break;
        	case '2': resultOutput = 'F';
            	      break;
			case 'Indeterminate': resultOutput = 'I';
					  break;
    	}			  	
		tmp['PID']['PID.8']['PID.8.1'] = resultOutput;



		//PID-T17 	Transform addresses
		//PID-T25	Transform EMAIL
		index=findFirst('HOME',msg['PID']['PID.11'],'PID.11.7');
		if (index > -1) {
			tmp['PID']['PID.11']['PID.11.1'] = msg['PID']['PID.11'][index]['PID.11.1'].toString();
			tmp['PID']['PID.11']['PID.11.2'] = msg['PID']['PID.11'][index]['PID.11.2'].toString();
			tmp['PID']['PID.11']['PID.11.3'] = msg['PID']['PID.11'][index]['PID.11.8'].toString();
			tmp['PID']['PID.11']['PID.11.4'] = msg['PID']['PID.11'][index]['PID.11.3'].toString();	
			tmp['PID']['PID.11']['PID.11.5'] = msg['PID']['PID.11'][index]['PID.11.5'].toString();
		}	
		index=findFirst('EMAIL',msg['PID']['PID.11'],'PID.11.7');
		if (index > -1) {
			tmp['PID']['PID.13'][0]['PID.13.4']=msg['PID']['PID.11'][index]['PID.11.1'].toString();
		}


		//PID-T25	Transform phone values
    	// MRD: 18/01/11
		index=findFirst('HOME',msg['PID']['PID.13'],'PID.13.2');

		if (index > -1) {
			// MRD: 10/02/11 Remove alpha characters from phone number 
			//tmp['PID']['PID.13'][0]['PID.13.1']=msg['PID']['PID.13'][index]['PID.13.1'].toString();
			tmp['PID']['PID.13'][0]['PID.13.1']=formatPhone(msg['PID']['PID.13'][index]['PID.13.1'].toString());
		}
		// MRD: 01/02/2011 Because the CM configuration is indeterminate check if MOBILE number 
    	// actually provide as PAGERPRSNL then overwrite with MOBILE if present
		index = findFirst('PAGERPRSNL',msg['PID']['PID.13'],'PID.13.2');
		if (index > -1) {
			// MRD: 10/02/11 Remove alpha characters from phone number 
			//tmp['PID']['PID.13'][1]['PID.13.1']=msg['PID']['PID.13'][index]['PID.13.1'].toString();
			tmp['PID']['PID.13'][1]['PID.13.1']=formatPhone(msg['PID']['PID.13'][index]['PID.13.1'].toString());
		}

    	index=findFirst('MOBILE',msg['PID']['PID.13'],'PID.13.2');
		if (index > -1) {
			// MRD: 10/02/11 Remove alpha characters from phone number 
			tmp['PID']['PID.13'][1]['PID.13.1']=formatPhone(msg['PID']['PID.13'][index]['PID.13.1'].toString());
		}


		//PID-T30 Pass first instance business number and email 
		// MRD: 10/02/11 Remove alpha characters from phone number 
    	tmp['PID']['PID.14']['PID.14.1']=formatPhone(msg['PID']['PID.14'][0]['PID.14.1'].toString());
		tmp['PID']['PID.14']['PID.14.4']=msg['PID']['PID.14'][0]['PID.14.4'].toString();
		tmp['PID']['PID.14']['PID.14.8']=msg['PID']['PID.14'][0]['PID.14.8'].toString();


		//PID-T41	Translate ethnic group code 99
		var ecode=msg['PID']['PID.22']['PID.22.1'].toString();
		if (ecode == '99') {
			tmp['PID']['PID.22']['PID.22.1']='""';
		}
		else {
			tmp['PID']['PID.22']['PID.22.1']=msg['PID']['PID.22']['PID.22.1'].toString();
		}


		//PID-T46 Pass Patient Death dt_tm if death indicator is not blank 
		//string=msg['PID']['PID.30']['PID.30.1'].toString();
		// MRD 21/01: PID.29 should be blanked if no patient death indicator 
    	//            not if there is a patient death indicator      

		if (!hasData(msg['PID']['PID.30']['PID.30.1'])) {
			tmp['PID']['PID.29']['PID.29.1']='""';
		}
		else {
			tmp['PID']['PID.29']['PID.29.1']=msg['PID']['PID.29']['PID.29.1'].toString();
		}
	}

	catch (e)
	{
    	throw new Error(" in tranPID " + e.name + ": " + e.message);
	}
}

