function tranPD1(){

	try {
		//MRD 10/03/11 CM may not send PD1 segment in A28!
		if(hasData(msg['PD1'])) {
				//Pass Organiser ID 
				//MRD 27/01/11 CM may pass multiple aliases for pracice in repeating PD1.3 sequence
				tmp['PD1']['PD1.3']['PD1.3.3']=msg['PD1']['PD1.3'][0]['PD1.3.3'].toString();
				//Pass Physician ID
				//MRD 27/01/11 CM may pass multiple aliases for gp in repeating PD1.4 sequence
        		// Assigning authority/type is not always supplied so take first one (TI outstanding)
				//tmp['PD1']['PD1.4']['PD1.4.1']=msg['PD1']['PD1.4']['PD1.4.1'].toString();
				//MRD 23/03/11 PD1.4 is optional
				if(hasData(msg['PD1']['PD1.4'])){
					tmp['PD1']['PD1.4']['PD1.4.1']=msg['PD1']['PD1.4'][0]['PD1.4.1'].toString();
				}
		}
	}

	catch (e) {
    	throw new Error(" in tranPD1 " + e.name + ": " + e.message);
	}
}

