step.script:
function tranPV1() {
/// if (eventValid(['A01','A02','A05'],'ADT')) {

	try {

		// put the facility id onto the channel map
		channelMap.put("facility",msg['PV1']['PV1.3']['PV1.3.4'].toString());

		//Pass Set ID 
		tmp['PV1']['PV1.1']['PV1.1.1']=msg['PV1']['PV1.1']['PV1.1.1'].toString();

		//PV1-T3 Pass point of service location or Facility ID if blank
		//MD 31/01/2011: Should test PV1.3.1 not PID.30.1 for blank
		//if (Blank(msg['PID']['PID.30']['PID.30.1'].toString())) {
		if (Blank(msg['PV1']['PV1.3'][0]['PV1.3.1'].toString())) {
			tmp['PV1']['PV1.3'][0]['PV1.3.1']=msg['PV1']['PV1.3'][0]['PV1.3.4'].toString();
		}
		else {
			tmp['PV1']['PV1.3'][0]['PV1.3.1']=msg['PV1']['PV1.3'][0]['PV1.3.1'].toString();
		}

		//PV1-T14 Set consulting doctor from attending doctor
		// MRD 10/04/2013 tmp['PV1']['PV1.9'][0]['PV1.9.1']=msg['PV1']['PV1.7'][0]['PV1.7.1'].toString();
		//Extract the NHS consultant number from PV1.7
		//and us it as the outbound consulting doctor and ordering provider
		for each (cons in msg['PV1']['PV1.7'] )
		{
			var auth = cons['PV1.7.9'].toString();
			if (auth == "NHSCONSULTNBR" ) {
				var consNumb = cons['PV1.7.1'].toString();
				tmp['PV1']['PV1.9']['PV1.9.1'] = consNumb;
				break;
			}
		}

		//Pass Admit Date/Time
		// MRD 21/01: Default date time if not supplied
		// if(!hasData(msg['PV1']['PV1.44']['PV1.44.1'])) tmp['PV1']['PV1.44']['PV1.44.1'] = "19000101010000";
		// MRD 02/02: Default admit D/T to event D/T if not porvided (some A05 messages have no Admit DT)
        // Use the event date time.
		if(!hasData(msg['PV1']['PV1.44']['PV1.44.1'])) 
            tmp['PV1']['PV1.44']['PV1.44.1'] = msg['EVN']['EVN.2']['EVN.2.1'].toString();
        else 
	    	tmp['PV1']['PV1.44']['PV1.44.1'] = msg['PV1']['PV1.44']['PV1.44.1'].toString();

	}

	catch (e) {
    	throw new Error(" in  tranPV1  " + e.name + ": " + e.message);
	}
}

