step.script:
function tranMRG() {

	try {

		//PID-T2	Output NOC mrn and nhs number only in that order
		tmp['MRG']['MRG.1'][0]['MRG.1.1']=getPID(msg['MRG']['MRG.1'],'NOC-MRN','MRN','MRG.1.4','MRG.1.5','MRG.1.1');
		tmp['MRG']['MRG.1'][0]['MRG.1.4']='RBF03';
		tmp['MRG']['MRG.1'][0]['MRG.1.5']='MR';
		// make nhs number 10 spaces if absent 
		var nhsnumber=getPID(msg['MRG']['MRG.1'],'NHSNBR','NHSNMBR','MRG.1.4','MRG.1.5','MRG.1.1');
		if (!Blank(nhsnumber)) {
			tmp['MRG']['MRG.1'][1]['MRG.1.1']=nhsnumber;
		}
		else	{
			tmp['MRG']['MRG.1'][1]['MRG.1.1']='          ';
		}
		tmp['MRG']['MRG.1'][1]['MRG.1.4']='NHS';
		tmp['MRG']['MRG.1'][1]['MRG.1.5']='NH';

		//PID-T3	Prefix ORH MRN's with NACS code
		//          *** NOT APPLICABLE for NOC RIS ***
	}

	catch (e) {
    	throw new Error(" in tranMRG " + e.name + ": " + e.message);
	}
}

