step.script:
// Finds the first element in sequence identified by type label 'string'
// Throws custom error if code exception 
function findFirst(label,SEGn,sub) {
	var idx = -1;

	try {
		if (SEGn !='') {
			var count = SEGn.length();		
			for (var i=0; i < count; i++) {
				if (SEGn[i].length() > 0) {
					// MRD 19/01/11
					//if (SEGn[sub][i].toString() == string) {			
					if (SEGn[i][sub].toString() == label) {
							idx=i;
							break;
					}
				}
			}
		}
	}
	catch (e) {
		throw new Error(" in helper function findFirst  " + label + " " + e.name + ": " + e.message);
	}
	finally {
		return idx;
	}
}

