step.script:
//Gets id from PID3 by assiginer code and type
//Throws custom error if code exception
function getPID(SEGn, assigner_code, type_code,pos1,pos2,pos3) {
	var pid = '';
	try {
		var pidcount = SEGn.length();
		for(var index = 0; index < pidcount; index++)
		{
			if(SEGn[pos1][index].toString() == assigner_code && SEGn[pos2][index].toString() == type_code) 
			{
				pid = SEGn[pos3][index].toString();
				break;
			}

		}
	}
	catch (e) {
		throw new Error(" in helper function getPID for " + type_code +  "  " + e.name + ": " + e.message);
	}
	finally {
		return pid;
	}
}

