// Returns true if string is empty or string is double quotes
// Throws custom error if code exception
function Blank(string) {
		var rc = false;
		try {
			if ((string=='')||(string=='""')) {
				rc = true;
			}
		}
	catch (e) {
		throw new Error(" in helper function Blank " + e.name + ": " + e.message);
	}
	finally {
		return rc;
	}
}

