// Throws custom error if code exception
function hasData(xmlNode) {
	var rc = false;
	try {
		//MD 21/01: Length is not a method 
		if  (xmlNode.length() > 0) {
			string=xmlNode.toString();
			if ((string!='')&&(string!='""')) {
			rc=true
			}
		}
	}	

	catch (e) {
		throw new Error(" in helper function hasData " + e.name + ": " + e.message);
	}
	finally {
		return rc;
	}
}

