step.script:
// remove alpha characters from phone number
function formatPhone(phoneNumber)
{
    var input = new String(phoneNumber);
	// regx to match any character that is not numeric
	var regX1 =  new RegExp("[^0-9]", "g");
	// regx to match 2 or more space characters
	var regX2 =  new RegExp("[ ]{2,}", "g");
	var output = "";


	//get rid of non-numeric
	input = input.replace(regX1, " ");
	//reduce spaces to one
	output = input.replace(regX2, " ");
    // truncate if single charecter only left
    if(output == " " || output.length == 0) output = '""';

    return output;
}

