try {

	var pass = false;
	var adtEvent =  msg['MSH']['MSH.9']['MSH.9.2'].toString();

	switch (adtEvent) {
		case 'A28' : // Admit patient
		case 'A31' : // Transfer patient
		case 'A34' : // Merge patient	
			pass = true;
			break;	

	    case 'A01' : // Admit patient	
		case 'A02' : // Transfer patient
		case 'A03' : // Discharge patient
        case 'A05' : // Pre-admit patient
		case 'A13' : // Cancel patient discharge
		case 'S12' : // New appointment
		case 'S13' : // updated appointment
			pass = (msg['PV1']['PV1.3']['PV1.3.4'].toString() == 'NOC');
			break;

		default :
			pass = false;
			break;
	}

	return pass;
}
catch (e) {
		logger.error("Channel " + getChannelName() + " ( Filter ADT Event ) " + e.name + ": " + e.message);
		throw e;
}

